import "./App.css";
import Navbar from "./Navbar";
import Home from "./Home";
import { useState, useEffect } from "react";
import MainContent from "./Components/MainContent";

function App() {
  const [search, SetSearch] = useState("");
  const [info, setInfo] = useState({});
  const [text, setText] = useState("");

  useEffect(() => {
    if (text) {
      fetch(`https://kitsu.io/api/edge/anime?filter[text]=${text}`)
        .then((response) => response.json())
        .then((response) => {
          setInfo(response);
        });
    }
  }, [text]);

  console.log(text);

  return (
    <div className="App">
      <h1>Hello World</h1>
      <Navbar />
      <br />
      <br />
      <Home />
      <MainContent value={text} onChange={(search) => setText(search)} />
      {info.data && (
        <ul>
          {info.data.map((anime) => (
            <li key={anime.id}>{anime.attributes.canonicalTitle}</li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
