import React from "react";
import { useState, useEffect } from "react";
import MangaContent from "./AnimeContent";
import MangaPaginations from "./AnimePaginations";
import qs from "qs";

const api = "https://kitsu.io/api/edge/";

const LIMIT = 12;

function SearchManga() {
  const [info, setInfo] = useState({});
  const [text, setText] = useState("");
  const [offset, setOffset] = useState(0);

  console.log(info);

  useEffect(() => {
    //setInfo({});
    const query = {
      page: {
        limit: LIMIT,
        offset,
      },
    };
    if (text) {
      query.filter = {
        text,
      };
    }

    fetch(`${api}manga?${qs.stringify(query)}`)
      .then((response) => response.json())
      .then((response) => {
        setInfo(response);
      });
  }, [text, offset]);

  return (
    <div>
      <MangaContent value={text} onChange={(search) => setText(search)} />
      {text && !info.data && <span>loading...</span>}
      {info.data && (
        <ul className="animes-list">
          {info.data.map((manga) => (
            <li key={manga.id}>
              <img
                src={manga.attributes.posterImage.small}
                alt={manga.attributes.canonicalTitle}
              />
              {manga.attributes.canonicalTitle}
            </li>
          ))}
        </ul>
      )}
      {info.meta && (
        <MangaPaginations
          limit={LIMIT}
          total={info.meta.count}
          offset={offset}
          setOffset={setOffset}
        />
      )}
    </div>
  );
}

export default SearchManga;
