import React from "react";
import { useState, useEffect } from "react";
import AnimeContent from "./AnimeContent";
import AnimePaginations from "./AnimePaginations";
import qs from "qs";
import { motion } from "framer-motion";

const api = "https://kitsu.io/api/edge/";

const LIMIT = 12;

function SearchAnime() {
  const [info, setInfo] = useState({});
  const [text, setText] = useState("");
  const [offset, setOffset] = useState(0);

  console.log(info);

  useEffect(() => {
    //setInfo({});
    const query = {
      page: {
        limit: LIMIT,
        offset,
      },
    };
    if (text) {
      query.filter = {
        text,
      };
    }

    fetch(`${api}anime?${qs.stringify(query)}`)
      .then((response) => response.json())
      .then((response) => {
        setInfo(response);
      });
  }, [text, offset]);

  return (
    <div>
      <AnimeContent value={text} onChange={(search) => setText(search)} />
      {text && !info.data && <span>loading...</span>}
      {info.data && (
        <ul className="animes-list">
          {info.data.map((anime) => (
            <li key={anime.id}>
              <img
                src={anime.attributes.posterImage.small}
                alt={anime.attributes.canonicalTitle}
              />
              <motion.div
                initial={{ height: 0 }}
                whileHover={{ height: 4 }}
                className="intro"
              >
                <h3>{anime.attributes.canonicalTitle}</h3>
              </motion.div>
            </li>
          ))}
        </ul>
      )}
      {info.meta && (
        <AnimePaginations
          limit={LIMIT}
          total={info.meta.count}
          offset={offset}
          setOffset={setOffset}
        />
      )}
    </div>
  );
}

export default SearchAnime;
